import routeMain from "./routes";

import {useDispatch, useSelector} from "react-redux";
import { loadMain } from "store/MainPage/actions";
import { selectFilms } from "store/MainPage/selectors";

import MainPageInfo from './MainPageInfo';

import "./style.scss";

const MainPage = () => {
    const dispatch = useDispatch();
    const filmsMain = useSelector(selectFilms);

    useEffect(() => {
        dispatch(loadMain());
    }, [dispatch]);

    const totalArray = filmsMain.splice(8, 9);
    return(
        <div className='MainPage_wrapp'>
            <div className='MainPage_wrapp2'>
                <h1 className='Main_name'>MOVIES PORTAL</h1>
                {filmsMain.length > 0 ? (
                    filmsMain.map(film => (
                        <MainPageInfo
                        props={film["show"]}
                        key={film.show.id}
                        />
                    ))
                ) : <div>пусто...</div>}
            </div>
        </div>
    )
}

export {routeMain};

export default MainPage
