import React from "react";

import prepareCountry from "utils/prepareCountry";
import prepareGenre from 'utils/prepareGenre';
import prepareImage from 'utils/prepareImage';
import prepareDate from 'utils/prepareDate';

import { routeMain as routeFilmDetail } from 'pages/FilmDetail';

import { NavLink } from "react-router-dom";

import "../style.scss";

import IShow from "types/IShow";

interface IProps {
    props: IShow;
}

const MainPageShow: React.FC<IProps> = ({props}) => {
    
    return (
            <NavLink className='filmsMain' to={routeFilmDetail(props.id.toString())}>
                                <img className="img" alt="image" src={prepareImage(props.image?.medium)}/>
                                <div className="text__inner">
                                    <div className="title"> {props.name} </div>
                                    <div> {prepareDate(props.premiered)} ({prepareCountry(props.network?.country?.name)}) </div>
                                    <div> {prepareGenre(props.genres?.join(', '))} </div>
                                </div>
                                
            </NavLink> 
    )
}

export default MainPageShow;
