import { Dispatch } from "redux"
import getFilms from "services/getFilms"
import getFilmsMainPage from "services/getFilms"
import { IStoreMain } from "./types"

export const getMainPageAction = (films: IStoreMain['films']) => { 
    return {
        type: "mainPage/getMainPage",
        payload: films,
    }
}

export const loadMain = () => async (dispatch: Dispatch) => { 
    try {
        const response = await getFilms('girls');
        dispatch(getMainPageAction(response.data));
    } catch(e) {
        console.log(e, "ошибка mainPageAction");
    }
}
