import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { loadSearch } from "store/SearchPage/actions";
import { selectFilms } from "store/SearchPage/selectors";

import MainPageShow from "pages/MainPage/MainPageShow";

import routeMain from "./routes";

import './style.scss';

import "../../assets/fonts/fonts.css";

import '../../assets/fonts/fonts.css';

import Vector from "assets/img/Vector.png";

const SearchPage = () => {
   const dispatch = useDispatch();
   const filmsArray = useSelector(selectFilms);

   const [value, setValue] = useState("");

   const functionOnSubmit = (e: any) => {
      e.preventDefault();
      if (value) {
         dispatch(loadSearch(value));
         setValue("");
      }
   };

   return (
      <div className="search-page_container">
         <div className="search-wrapper">
            <form className="search__inner" onSubmit={(e) => functionOnSubmit(e)}>
               <input
                  type="text"
                  className="input__search"
                  placeholder="Пиши тут"
                  onChange={(e) => setValue(e.target.value)}
                  value={value}
               />
               {/* <button 
               type="submit" 
               className="button__search" 
               value="search">
                  <img
                     src={Vector}
                     alt='Vector'
                  />
               </button> */}
               <button
                  type="submit"
                  className="icon-search icon-style"
                  title="смело можно тыкать!"
                ></button>
            </form>
            <button
               type="button"
               className="button_clear"
               onClick={() => dispatch(loadSearch(""))}
            >
               Очистить поиск
            </button>
         </div>
         <div>
            {filmsArray.length > 0 ? (
               filmsArray
                  .slice(0, 8)
                  .map((film) => (
                     <MainPageShow props={film["show"]} key={film.show.id} />
                  ))
            ) : (
               <div className="search_result-none">Введите ваш запрос</div>
            )}
         </div>
      </div>
   );
};

export { routeMain };

export default SearchPage;
