import React, { useEffect } from 'react'


import routeMain from './routes'

import {useDispatch, useSelector} from "react-redux";
import { loadDetails } from "store/DetailPage/actions";
import { selectDetails } from "store/DetailPage/selectors";

import './style.scss'

import FilmListShow from './FilmListShow';

const FilmListPage = () => {
   const dispatch = useDispatch();
   const arrayListFilms = useSelector(selectDetails);

   useEffect(() => {
       dispatch(loadDetails());
   }, [dispatch])

   return(
           <div>
               {arrayListFilms.length > 0 ? (
                   arrayListFilms.map(detail => (
                        <FilmListShow
                        list={detail}
                        key={detail.id}
                        />
                   ))
               ) : <div>пусто</div>}
           </div>
   )
}

export { routeMain }
export default FilmListPage
