import React, { useEffect } from 'react'

import routeMain from "./routes";

import { useDispatch, useSelector } from "react-redux";
import { loadMain } from "store/MainPage/actions";
import { selectFilms } from "store/MainPage/selectors";

import MainPageShow from './MainPageShow';

import "./style.scss";

const MainPage = () => {
    const dispatch = useDispatch();
    const filmsMain = useSelector(selectFilms);

    useEffect(() => {
        dispatch(loadMain());
    }, [dispatch]);

    // filmsMain.splice(8, 9);
    const filmsArray = filmsMain.slice(0, 8);

    return (
        <section>
            <h1 className='main_name'>MOVIES PORTAL</h1>
            <div className='main-page_wrapp'>
                    {filmsMain.length > 0 ? (
                        filmsMain.map(film => (
                            <MainPageShow
                                props={film["show"]}
                                key={film.show.id}
                            />
                        ))
                    ) : <div>пусто...</div>}
            </div>
        </section>
    )
}

export { routeMain };

export default MainPage
