import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { loadMain } from "store/MainPage/actions";
import { selectFilms } from "store/MainPage/selectors";
import MainPage from "..";
import prepareCountry from "utils/prepareCountry";
import prepareGenre from 'utils/prepareGenre';
import prepareImage from 'utils/prepareImage';
import prepareDate from 'utils/prepareDate';
import { routeMain as routeFilmDetail } from 'pages/FilmDetail';
import { NavLink } from "react-router-dom";

const MainPageInfo = () => {
    const dispatch = useDispatch();
    const filmsArray = useSelector(selectFilms);

    useEffect(() => {
        dispatch(loadMain());
    }, [dispatch])

    return (
        <div>
            {filmsArray.length > 0 ? (
                filmsArray.map((film) => {
                    <NavLink className='filmsMain' to={routeFilmDetail(film.show.id.toString())}>
                        <img className="img" alt="image" src={prepareImage(film.show.image?.medium)} />
                        <div> {film.show.name} </div>
                        <div> {prepareDate(film.show.premiered)} ({prepareCountry(film.show.network?.country?.name)}) </div>
                        <div> {prepareGenre(film.show.genres?.join(', '))} </div>
                    </NavLink>
                })
            ) : <div>пусто</div>}

        </div>
    )
}

export default MainPageInfo;
