import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'

import { selectDetailsAboutFilm } from 'store/DetailAboutFilm/selectors'
import { loadDetailFilm } from 'store/DetailAboutFilm/actions'
import routeMain from './routes';

import prepareImage from 'utils/prepareImage';
import prepareRating from 'utils/prepareRating';
import prepareCountry from 'utils/prepareCountry';
import prepareDate from 'utils/prepareDate';
import prepareSummary from 'utils/prepareSummary'
import prepareGenre from 'utils/prepareGenre'

import star from "assets/img/star.png"

import './style.scss'

import { ID } from 'types/ID'

import { useParams } from 'react-router-dom'


const FilmDetail = () => {
  const { id } = useParams<ID>();
  const dispatch = useDispatch();
  const detailArray = useSelector(selectDetailsAboutFilm);
  useEffect(() => {
    dispatch(loadDetailFilm(id))
  }, [id, dispatch]);

  return (
    <section>
      <div>
        {detailArray.length > 0 ? (
          detailArray.map((detail) => (
            <div className='detail__wrapp'>
              <div className='detail__inner'>
                <div><img className="detail__img" src={prepareImage(detail.image?.medium)} /></div>
                <div className='detail_info'>
                  <div className='detail__title'>
                    <div className='detail__title-name'>
                      <span className='name'>{detail.name}</span>

                      </div>
                    <div className='rating'>{}{prepareRating(detail.rating?.average)}/10</div>
                  </div>
                  <p className='date'><span className='detail_info-item'>Год выхода: </span>{prepareDate(detail.premiered)}</p>
                  <p className="country"><span className='detail_info-item'>Страна: </span>{prepareCountry(detail.network?.country?.name)}</p>
                  <p className='genre'><span className='detail_info-item'>Жанр: </span>{prepareGenre(detail.genres.join(', '))}</p>
                  <p className='summary'><span className='detail_info-item'>Описание: </span>{prepareSummary(detail.summary)}</p>
                </div>
              </div>
            </div>
          ))
        ) : <div>Пусто</div>}
      </div>
    </section>
  )
}

export { routeMain }
export default FilmDetail
