import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";

import { selectDetailsAboutFilm } from "store/detail/selectors";
import { loadDetail } from "store/detail/actions";
import routeMain from "./routes";

import prepareImage from "utils/prepareImage";
import prepareRating from "utils/prepareRating";
import prepareCountry from "utils/prepareCountry";
import prepareDate from "utils/prepareDate";
import prepareSummary from "utils/prepareSummary";
import prepareGenre from "utils/prepareGenre";

import star from "assets/img/star.png";

import { IStoreDetail } from "store/detail/types";

import "./style.scss";

import { ID } from "types/ID";

import { useParams } from "react-router-dom";
import IShow from "types/IShow";

const FilmDetail = () => {
   const { id } = useParams<ID>();
   const dispatch = useDispatch();
   const detail = useSelector(selectDetailsAboutFilm);
   useEffect(() => {
      dispatch(loadDetail(id));
   }, [id, dispatch]);

   return (
      <section>
         <div>
            <div className="container">
               <div className="detail__wrapper">
                  <div>
                     {prepareImage(detail?.image?.medium, "detail__img")}
                  </div>
                  <div className="detail_info">
                     <div className="detail__title">
                        <div className="detail__title-name">
                           {detail?.name}
                        </div>
                        <div className="detail__info-rating">
                           <img
                              className="rating_star-img"
                              src={star}
                              alt="star"
                           />
                           {prepareRating(detail?.rating?.average)}/10
                        </div>
                     </div>
                     <div className="detail__info-summary">
                        <div className="detail_info_col-1">
                           <p>Год выхода: </p>
                           <p>Страна: </p>
                           <p>Жанр: </p>
                           <p>Описание: </p>
                        </div>
                        <div className="detail_info_col-2">
                           <p className="detail_info-prepare">
                              {prepareDate(detail?.premiered)}
                           </p>
                           <p className="detail_info-prepare">
                              {prepareCountry(detail?.network?.country?.name)}
                           </p>
                           <p className="detail_info-prepare">
                              {prepareGenre(detail?.genres?.join(", "))}
                           </p>
                           <p className="detail_info-prepare-summary">
                              {prepareSummary(detail?.summary)}
                           </p>
                        </div>
                     </div>
                  </div>
               </div>
            </div>
         </div>
      </section>
   );
};

export { routeMain };
export default FilmDetail;
