import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import FilmListShow from "pages/FilmListPage/FilmListShow";

import routeMain from "./routes";

import { loadSearch } from "store/SearchPage/actions";
import { selectFilms } from "store/SearchPage/selectors";


const SearchPage = () => {
    const dispatch = useDispatch();
    const filmsArray = useSelector(selectFilms)

    const [value, setValue] = useState('');

    const functionOnSubmit = (e) => {
        e.preventDefault();
        if(value) {
            dispatch(loadSearch(value));
            setValue('');
        } 
    }


    return (
        
        <div className='container'>
            <form className="Search__inner" onSubmit={e => functionOnSubmit(e)}>
                <input type='text' className="input__search" placeholder="example: animals" onChange={e => setValue(e.target.value)} value={value}/>
                <button type='submit' className="button__search" value='search'>+</button>
            </form>
        </div>   
        


    )
}



export { routeMain };

export default SearchPage;
