import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import FilmListShow from "pages/FilmListPage/FilmListShow";

import routeMain from "./routes";

import { loadDetails } from "store/DetailPage/actions";
import { selectDetails } from "store/DetailPage/selectors";

const SearchPage = () => {
    const dispatch = useDispatch();
    const arrayListFilms = useSelector(selectDetails);

    useEffect(() => {
        dispatch(loadDetails());
    }, [dispatch])

    const [value, setValue] = useState('');
    const filmsArrayFilter = arrayListFilms.filter(film => {
        return film.genres.toString().toLowerCase().includes(value.toLowerCase());
    })



    return (
        <div className='container'>
            
            <form className="Search__inner">
                <input className="input" placeholder="example: animals" type='text' onChange={(e) => setValue(e.target.value)} />
                <button className="btn" type="button">Поиск</button>
            </form>
            {
            filmsArrayFilter.map(film => (
                        film
            ))
            }
        </div>


    )
}



export { routeMain };

export default SearchPage;
