import { Dispatch } from "redux"
import getFilms from "services/getFilms"
import { IStoreMain } from "./types"

export const getSearchPageAction = (films: IStoreMain['films']) => { 
    return {
        type: "searchPage/getSearchPage",
        payload: films,
    }
}

export const loadSearch = (value: string) => async (dispatch: Dispatch) => { 
    try {
        const response = await getFilms(value);
        dispatch(getSearchPageAction(response.data));
    } catch(e) {
        console.log(e, "ошибка SearchPageAction");
    }
}
