import {  legacy_createStore as createStore, applyMiddleware, combineReducers } from "redux";
import {composeWithDevTools} from "redux-devtools-extension";
import thunk from "redux-thunk";
import reducerForDetailPage from "./DetailPage/reducer";
import reducerForMainPage from "./MainPage/reducer";
import reducerForDetailFilm from "./DetailAboutFilm/reducer";
import reducerForSearchPage from "./SearchPage/reducer";

const rootReducer = combineReducers({reducerForDetailPage, reducerForMainPage, reducerForDetailFilm, reducerForSearchPage});

const store = createStore(rootReducer, composeWithDevTools(applyMiddleware(thunk)));

export default store;
