import { Dispatch } from "redux"
import getFilmsMainPage from "services/getFilmsMainPage"
import { IStoreMain } from "./types"

export const getMainPageAction = (films: IStoreMain['payload']) => { //(payload: IMainPage)
    return {
        type: "mainPage/getMainPage",
        payload: films,
    }
}

export const loadMain = () => async (dispatch: Dispatch) => { //(dispatch:Dispatch)
    try {
        const response = await getFilmsMainPage();
        dispatch(getMainPageAction(response.data));
    } catch(e) {
        console.log(e, "ошибка mainPageAction");
    }
}
