import React, { useState, useEffect } from 'react'

import routeMain from "./routes";

import {useDispatch, useSelector} from "react-redux";
import { loadMain } from "store/MainPage/actions";
import { selectFilms } from "store/MainPage/selectors";


import "./style.scss";

import prepareGenre from "utils/prepareGenre"
import prepareCountry from "utils/prepareCountry";
import prepareImage from 'utils/prepareImage';
import prepareDate from "utils/prepareDate";
import { NavLink } from "react-router-dom";

const MainPage = () => {
    const dispatch = useDispatch();
    const filmsMain = useSelector(selectFilms);

    useEffect(() => {
        dispatch(loadMain());
    }, [dispatch]);
    return(
        <div>
            <div>
                {filmsMain.length > 0 ? (
                    filmsMain.map(film => (
                            <div className='filmsMain' key={film.id}>
                                <img className="img" src={prepareImage(film.show.image?.medium)}/><br/>
                                {film.show.name} <br/> 
                                {prepareDate(film.show.premiered)} ({prepareCountry(film.show.network?.country.name)})<br/> 
                                {prepareGenre(film.show.genres.join(', '))}
                            </div> 
                    ))
                ) : <div>пусто...</div>}
            </div>
        </div>
    )
}

export {routeMain};

export default MainPage
