import {AnyAction} from "redux";

const initialState = {
    detailFilm: [],
};

const reducerForDetailFilm = (state = initialState, action) => {
    switch(action.type) {
        case "detailAboutFilm/getDetailFilm" :
            return {...state, detailFilm: [...action.payload]}
        default:
            return state;
    }
}

export default reducerForDetailFilm;
