import { Dispatch } from "redux";
import getDetail from "../../services/getDetail";
import { IStoreDetail } from "./types";

export const getDetailAction = (detailFilm: IStoreDetail["detailFilm"]) => {
   return {
      type: "detail/getDetail",
      payload: detailFilm,
   };
};

export const loadDetail = (value: string) => async (dispatch: Dispatch) => {
   try {
      let Detail = [];
      const response = await getDetail('/' + value);
      Detail[0] = response.data;
      dispatch(getDetailAction(Detail));
   } catch (e) {
      console.log(e, "error loadDetailFilmAction");
   }
};
