import React from "react";
import { NavLink } from "react-router-dom";
import { routeMain as routeFilmDetail } from "pages/FilmDetail"
import prepareImage from "utils/prepareImage";
import prepareGenre from "utils/prepareGenre";
import IDetailPage from "types/IDetailPage"
import "../style.scss"

interface IList {
    list: IDetailPage
}

const FilmListShow: React.FC<IList> = ({ list }) => {

    return (
        <NavLink className="list-page__item" key={list.id} to={routeFilmDetail(list.id.toString())}>
                    <img className="list-page__item-img" src={prepareImage(list.image?.medium)} />
                <div className="list-page__item-info">
                    <p className="list-page__item-info-title">{list.name}</p>
                    <p className="list-page__item-info-subtitle">{prepareGenre(list.genres.join(', '))}</p>
            </div>
           
        </NavLink>
    )
}

export default FilmListShow;
