import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'

import { selectDetailsAboutFilm } from 'store/DetailAboutFilm/selectors'
import { loadDetailFilm } from 'store/DetailAboutFilm/actions'
import routeMain from './routes';

import prepareImage from 'utils/prepareImage';
import prepareRating from 'utils/prepareRating';
import prepareCountry from 'utils/prepareCountry';
import prepareDate from 'utils/prepareDate';
import prepareSummary from 'utils/prepareSummary'
import prepareGenre from 'utils/prepareGenre'

import './style.scss'
import { useParams } from 'react-router-dom'


const FilmDetail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const ShowDetail = useSelector(selectDetailsAboutFilm);
  useEffect(() => {
    dispatch(loadDetailFilm(id))
  }, [id, dispatch]);

  return (
    <div>
      <div>
        {ShowDetail.length > 0 ? (
          ShowDetail.map((detail) => (
            <div>
              <img className="img" src={prepareImage(detail.image?.medium)} />
              <p className='name'>{detail.name}</p>
              <p className='rating'>{prepareRating(detail.rating.average)}/10</p>
              <p className='date'> Год выхода: {prepareDate(detail.premiered)}</p>
              <p className="country">Страна: {prepareCountry(detail.network?.country.name)}</p>
              <p className='genre'>Жанр: {prepareGenre(detail.genres.join(', '))}</p>
              <p className='summary'>Описание: {prepareSummary(detail.summary)}</p>
            </div>
          ))
        ) : <div>Пусто</div>}
      </div>
    </div>
  )
}

export { routeMain }
export default FilmDetail
