import React, { useState, useEffect } from 'react'

import routeMain from "./routes";

import {useDispatch, useSelector} from "react-redux";
import { loadMain } from "store/MainPage/actions";
import { selectFilms } from "store/MainPage/selectors";


import "./style.scss";

import prepareCountry from '../../../src/utils/prepareCountry';
import prepareGenre from 'utils/prepareGenre';
import prepareImage from 'utils/prepareImage';
import prepareDate from 'utils/prepareDate';
import { NavLink } from "react-router-dom";

import { routeMain as routeFilmDetail } from 'pages/FilmDetail';

const MainPage = () => {
    const dispatch = useDispatch();
    const filmsMain = useSelector(selectFilms);

    useEffect(() => {
        dispatch(loadMain());
    }, [dispatch]);
    return(
        <div className='MainPage_wrapp'>
            <div className='MainPage_wrapp2'>
                <h1 className='Main_name'>MOVIES PORTAL</h1>
                {filmsMain.length > 0 ? (
                    filmsMain.map(film => (
                            <NavLink className='filmsMain' key={film.id} to={routeFilmDetail(film.show.id.toString())}>
                                <img className="img" src={prepareImage(film.show.image?.medium)}/><br/>
                                <p>{film.show.name}</p> <br/> 
                                <p>{prepareDate(film.show.premiered)} ({prepareCountry(film.show.network?.country.name)})</p><br/> 
                                <p>{prepareGenre(film.show.genres.join(', '))}</p>
                            </NavLink> 
                    ))
                ) : <div>пусто...</div>}
            </div>
        </div>
    )
}

export {routeMain};

export default MainPage
