import React, { useState, useEffect } from 'react'


import routeMain from './routes'
import {routeMain as routeFilmDetail} from "pages/FilmDetail"

import {useDispatch, useSelector} from "react-redux";
import { loadDetails } from "store/DetailPage/actions";
import { selectDetails } from "store/DetailPage/selectors";

import prepareImage from "../../utils/prepareImage";
import prepareGenre from "../../utils/prepareGenre";

import './style.scss'
import { NavLink } from 'react-router-dom';
import FilmListShow from './FilmListShow';

const FilmListPage = () => {
   const dispatch = useDispatch();
   const arrayListFilms = useSelector(selectDetails);

   useEffect(() => {
       dispatch(loadDetails());
   }, [dispatch])
   console.log(arrayListFilms)

   return(
           <div>
               {arrayListFilms.length > 0 ? (
                   arrayListFilms.map(detail => (
                        <FilmListShow
                        list={detail}
                        key={detail.id}
                        />
                   ))
               ) : <div>пусто</div>}
           </div>
   )
}

export { routeMain }
export default FilmListPage
