import { NavLink } from "react-router-dom";
import {routeMain as routeFilmDetail} from "pages/FilmDetail"
import prepareImage from "utils/prepareImage";
import prepareGenre from "utils/prepareGenre";


const FilmListShow = (list) => {

    return (
        <div>
            <h1>Выбранная категория: <span>Animals</span></h1>
            <NavLink key={list.id} to={routeFilmDetail(list.id.toString())}>
                <img className="img" src={prepareImage(list.image?.medium)}/>
                <h2 className="#">{list.name}
                {prepareGenre(list.genres.join(', '))}</h2>
            </NavLink>
        </div>
    )
}

export default FilmListShow;


