import { Dispatch } from "redux"
import getFilmsDetailPage from "services/getFilmsDetailPage"
// import { IMainPage } from "types/IMainPage"

export const getDetailAction = (details) => { //payload: IMainPage
    return {
        type: 'detailPage/getDetail',
        payload: details,
    }
}

export const loadDetails = () => async (dispatch: Dispatch) => { // dispatch: Dispatch
    try {
        const response = await getFilmsDetailPage();
        dispatch(getDetailAction(response.data));
    } catch (e) {
        console.log(e, 'произошла ошибка, строка 15');
    }
}
