import { Dispatch } from "redux";
import getDetailsAboutFilm from "../../services/getDetailsAboutFilm";
import { IStoreDetail } from "./types";

export const getDetailFilmAction = (detailFilm: IStoreDetail["detailFilm"]) => {
   return {
      type: "detailAboutFilm/getDetailFilm",
      payload: detailFilm,
   };
};

export const loadDetailFilm = (value: string) => async (dispatch: Dispatch) => {
   try {
      let Detail = [];
      const response = await getDetailsAboutFilm(value);
      Detail[0] = response.data;
      dispatch(getDetailFilmAction(Detail));
   } catch (e) {
      console.log(e, "error loadDetailFilmAction");
   }
};
