import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { loadSearch } from "store/SearchPage/actions";
import { selectFilms } from "store/SearchPage/selectors";

import MainPageShow from "pages/MainPage/MainPageShow";

import routeMain from "./routes";



const SearchPage = () => {
    const dispatch = useDispatch();
    const filmsArray = useSelector(selectFilms)

    const [value, setValue] = useState('');

    const functionOnSubmit = (e:any) => {
        e.preventDefault();
        if(value) {
            dispatch(loadSearch(value));
            setValue('');
        } 
    }

    const clearSearch = () => {
       setValue('');
       dispatch(loadSearch(value));
    }


    return (
        <div className='container'>
            <form className="Search__inner" onSubmit={e => functionOnSubmit(e)}>
                <input type='text' className="input__search" placeholder="example: animals" onChange={e => setValue(e.target.value)} value={value}/>
                <button type='submit' className="button__search" value='search'>+</button>
            </form>
            <button type="button" className="button_clear" onClick={() => clearSearch()}>Очистить поиск</button>
            <div>
                {filmsArray.length > 0 ? (
                    filmsArray.map(film => (
                        <MainPageShow
                        props={film["show"]}
                        key={film.show.id}
                        />
                    ))
                ) : <div>Введите ваш запрос</div>}
            </div>
        </div> 
  
        


    )
}



export { routeMain };

export default SearchPage;
