import { Dispatch } from "redux"
import getFilmsMainPage from "services/getFilmsMainPage"
import { IStoreMain } from "./types"

export const getSearchPageAction = (films: IStoreMain['films']) => { 
    return {
        type: "searchPage/getSearchPage",
        payload: films,
    }
}

export const loadSearch = () => async (dispatch: Dispatch) => { 
    try {
        const response = await getFilmsMainPage('girls');
        dispatch(getSearchPageAction(response.data));
    } catch(e) {
        console.log(e, "ошибка mainPageAction");
    }
}
