import { Dispatch } from "redux";
import getDetailsAboutFilm from '../../services/getDetailsAboutFilm';
import { IStoreDetail } from "./types";

export const getDetailFilmAction = (payload) => { //payload: IStoreDetail['payload']
    return {
        type: 'detailAboutFilm/getDetailFilm',
        payload,
    }
}

export const loadDetailFilm = () => async (dispatch) => { //dispatch:Dispatch)
    try {
        const response = await getDetailsAboutFilm();
        dispatch(getDetailFilmAction(response.data));
    } catch (e) {
        console.log(e, 'произошла ошибка, строка 15');
    }
}
