import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { loadSearch } from "store/SearchPage/actions";
import { selectFilms } from "store/SearchPage/selectors";

import MainPageShow from "pages/MainPage/MainPageShow";

import routeMain from "./routes";

const SearchPage = () => {
   const dispatch = useDispatch();
   const filmsArray = useSelector(selectFilms);

   const [value, setValue] = useState("");

   const functionOnSubmit = (e: any) => {
      e.preventDefault();
      if (value) {
         dispatch(loadSearch(value));
         setValue("");
      }
   };

   return (
      <div className="container">
         <div>
            <form className="search__inner" onSubmit={(e) => functionOnSubmit(e)}>
            <input
               type="text"
               className="input__search"
               placeholder="example: animals"
               onChange={(e) => setValue(e.target.value)}
               value={value}
            />
            <button type="submit" className="button__search" value="search">
               Найти
            </button>
         </form>
         <button
            type="button"
            className="button_clear"
            onClick={() => dispatch(loadSearch(""))}
         >
            Очистить поиск
         </button>
         <div>
         </div>
         
            {filmsArray.length > 0 ? (
               filmsArray
                  .slice(0, 8)
                  .map((film) => (
                     <MainPageShow props={film["show"]} key={film.show.id} />
                  ))
            ) : (
               <div>Введите ваш запрос</div>
            )}
         </div>
      </div>
   );
};

export { routeMain };

export default SearchPage;
