import routeMain from "./routes";

import {useDispatch, useSelector} from "react-redux";
import { loadMain } from "store/MainPage/actions";
import { selectFilms } from "store/MainPage/selectors";

import MainPageInfo from './MainPageInfo';

import "./style.scss";

import prepareCountry from '../../../src/utils/prepareCountry';
import prepareGenre from 'utils/prepareGenre';
import prepareImage from 'utils/prepareImage';
import prepareDate from 'utils/prepareDate';
import { NavLink } from "react-router-dom";

import IShow from '../../types/IShow'

import { routeMain as routeFilmDetail } from 'pages/FilmDetail';

const MainPage = () => {
    const dispatch = useDispatch();
    const filmsMain = useSelector(selectFilms);

    useEffect(() => {
        dispatch(loadMain());
    }, [dispatch]);
    return(
        <div className='MainPage_wrapp'>
            <div className='MainPage_wrapp2'>
                <h1 className='Main_name'>MOVIES PORTAL</h1>
                {filmsMain.length > 0 ? (
                    filmsMain.map(film => (
                        <MainPageInfo
                        props={film}
                        // id={film.show.id}
                        // image={film.show.image?.medium}
                        // name={film.show.name}
                        // premiered={film.show.premiered}
                        // country={film.show.network?.country?.name}
                        // genre={film.show.genres?.join(', ')}
                        />
                    ))
                ) : <div>пусто...</div>}
            </div>
        </div>
    )
}

export {routeMain};

