import React, { useState, useEffect } from 'react'


import routeMain from './routes'
import {routeMain as routeFilmDetail} from "pages/FilmDetail"

import {useDispatch, useSelector} from "react-redux";
import { loadDetails } from "store/DetailPage/actions";
import { selectDetails } from "store/DetailPage/selectors";

import prepareImage from "../../utils/prepareImage";
import prepareGenre from "../../utils/prepareGenre";

import './style.scss'
import { NavLink } from 'react-router-dom';

const FilmListPage = () => {
   const dispatch = useDispatch();
   const filmsDetail = useSelector(selectDetails);

   useEffect(() => {
       dispatch(loadDetails());
   }, [dispatch])

   return(
           <div>
               <h1 className='category__name'>Выбранная категория: <span>Animals</span></h1>
               {filmsDetail.length > 0 ? (
                   filmsDetail.map(film => (
                           <NavLink key={film.id} to={routeFilmDetail(film.id.toString())}>
                               <img className="img" src={prepareImage(film.image?.medium)}/><br/>
                               <p>{film.name} <br/>
                                {prepareGenre(film.genres.join(', '))}</p>
                           </NavLink> 

                       
                   ))
               ) : <div>пусто...</div>}
       </div>
   )
}

export { routeMain }
export default FilmListPage
