import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import FilmListShow from "pages/FilmListPage/FilmListShow";

import routeMain from "./routes";

import { loadDetails } from "store/DetailPage/actions";
import { selectDetails } from "store/DetailPage/selectors";
import getFilmsMainPage from "services/getFilmsMainPage";
import { loadMain } from "store/MainPage/actions";


const SearchPage = () => {
    const dispatch = useDispatch();
    const arrayListFilms = useSelector(selectDetails);



    const [value, setValue] = useState('');

    useEffect(() => {
        dispatch(loadMain(value));
    }, [value, dispatch])

    const functionOnSubmit = (e) => {
        e.preventDefault();
        if(value) {
            setValue(value);
            console.log(value);
        } else {
            setValue('');
        }
    }


    return (
        
        <div className='container'>
            <form className="Search__inner" onSubmit={e => functionOnSubmit(e)}>
                <input type='text' className="input__search" placeholder="example: animals" onChange={e => setValue(e.target.value)} value={value}/>
                <button type='submit' className="button__search" value='search'>+</button>
            </form>
        </div>   
        


    )
}



export { routeMain };

export default SearchPage;
