import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { loadMain } from "store/MainPage/actions";
import { selectFilms } from "store/MainPage/selectors";
import MainPage from "..";
import prepareCountry from "utils/prepareCountry";
import prepareGenre from 'utils/prepareGenre';
import prepareImage from 'utils/prepareImage';
import prepareDate from 'utils/prepareDate';
import { routeMain as routeFilmDetail } from 'pages/FilmDetail';
import { NavLink } from "react-router-dom";

const MainPageInfo = (props) => {
    
    return (
        <div>
            <NavLink className='filmsMain' to={routeFilmDetail(props.id.toString())}>
                                <img className="img" alt="image" src={prepareImage(props.img)}/>
                                <div> {props.name} </div>
                                <div> {prepareDate(props.premiered)} ({prepareCountry(props.country)}) </div>
                                <div> {prepareGenre(props.genre)} </div>
            </NavLink> 
        </div>
    )
}

export default MainPageInfo;
 



