import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'

import { selectDetailsAboutFilm } from 'store/DetailAboutFilm/selectors'
import { loadDetailFilm } from 'store/DetailAboutFilm/actions'
import routeMain from './routes';

import prepareImage from 'utils/prepareImage';
import prepareRating from 'utils/prepareRating';
import prepareCountry from 'utils/prepareCountry'


import './style.scss'
import { useParams } from 'react-router-dom'

const FilmDetail = () => {
  const {id} = useParams();
  const dispatch = useDispatch();
  const ShowDetail = useSelector(selectDetailsAboutFilm);
  useEffect(() => {
    dispatch(loadDetailFilm(id))
  }, [id, dispatch]);

   return (
     <div>
        <div>
          {ShowDetail.length > 0 ? (
              ShowDetail.map((detail) => (
                <div>
                <img className="img" src={prepareImage(detail.image?.medium)}/>
                <p className='name'>{detail.name}</p>
                <p className='rating'>{prepareRating(detail.rating.average)}</p>
                <p className="country">Страна: {detail.network?.country.name}</p>
                </div>
              ))
       ) : <div>Пусто</div>}
        </div>
     </div>
    // <img className="img" src={prepareImage(film.show.image?.medium)}/><br/>
    // <p>{film.show.name}</p> <br/> 
    // <p>{prepareDate(film.show.premiered)} ({prepareCountry(film.show.network?.country.name)})</p><br/> 
    // <p>{prepareGenre(film.show.genres.join(', '))}</p>
   )
}

export { routeMain }
export default FilmDetail
